changeSet.sql:
			GRANT UPDATE ON TABLE binaries TO ${db.server_permanent_delete_users_group};
			GRANT ${db.server_permanent_delete_user} TO ${db.liquibase_user};
			SET ROLE ${db.server_permanent_delete_user};
			UPDATE binaries SET binary_oid = lo_from_bytea(0, binary_data) WHERE binary_data IS NOT NULL;
			DO '
			DECLARE
				lo_oid oid;
			BEGIN
				FOR lo_oid IN SELECT binary_oid FROM binaries LOOP
					EXECUTE format(''GRANT SELECT, UPDATE ON LARGE OBJECT %s TO ${db.server_users_group};'', lo_oid);
				END LOOP;
			END ';
			UPDATE binaries SET binary_size = b.size
			FROM (
    			SELECT binary_id, lo_lseek(lo_open(binary_oid, 262144), 0, 2) AS size FROM binaries
			) AS b
			WHERE binaries.binary_id = b.binary_id;
			RESET ROLE;
			REVOKE ${db.server_permanent_delete_user} FROM ${db.liquibase_user};
			REVOKE UPDATE ON TABLE binaries FROM ${db.server_permanent_delete_users_group};

ALTER TABLE current_binaries OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE current_binaries TO ${db.liquibase_user};
GRANT SELECT ON TABLE current_binaries TO ${db.server_users_group};

ALTER TABLE all_resources OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE all_resources TO ${db.liquibase_user};
GRANT SELECT ON TABLE all_resources TO ${db.server_users_group};

ALTER TABLE all_read_access_resources OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE all_read_access_resources TO ${db.liquibase_user};
GRANT SELECT ON TABLE all_read_access_resources TO ${db.server_users_group};
