changeSet.sql:
ALTER TABLE naming_systems OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE naming_systems TO ${db.liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE naming_systems TO ${db.server_users_group};
GRANT SELECT, DELETE ON TABLE naming_systems TO ${db.server_permanent_delete_users_group};
CREATE INDEX naming_system_id_index ON naming_systems USING btree (naming_system_id);
CREATE INDEX naming_system_index ON naming_systems USING gin (naming_system);
CREATE INDEX naming_system_id_version_index ON naming_systems USING btree (naming_system_id, version);

ALTER TABLE current_naming_systems OWNER TO ${db.liquibase_user};
GRANT ALL ON TABLE current_naming_systems TO ${db.liquibase_user};
GRANT SELECT ON TABLE current_naming_systems TO ${db.server_users_group};
